INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (4779, 175, 11, 6, '2023-07-15', 129, 'No citado',null, '2', 'Primer partido, se le vio muy nervioso en su juego, en su toma de decisiones. ');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (4780, 175, 11, 6, '2023-07-15', 130, 'Titular',null, '3', 'No fue ni el mejor ni el peor partido de Izan. Se mostro frustrado por tener en la marca 2 jugadores. Debe mejorar en su toma de decisiones en ofensiva, cuando asociarse y cuando buscar el mano a mano,');
