INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (4782, 176, 11, 6, '2023-07-22', 109, 'Suplente ingresa',null, '4', 'Entre muy bien, justo en un tiro libre, posicionandose de buena manera. ofensivamente tuvo buenos inicios estaticos y dinamicos, generando lineas de pase constantemente. Defensivamente estuvo atento al juego largo del rival. Comunico bastante.');
